import{_ as l,c as i,a2 as e,o as t}from"./chunks/framework.0Bx0CPYg.js";const m=JSON.parse('{"title":"day1 - 12.11","description":"","frontmatter":{},"headers":[],"relativePath":"front-end/杂货/vim.md","filePath":"front-end/杂货/vim.md","lastUpdated":1734418967000}'),o={name:"front-end/杂货/vim.md"};function r(d,a,n,p,s,h){return t(),i("div",null,a[0]||(a[0]=[e('<h1 id="day1-12-11" tabindex="-1">day1 - 12.11 <a class="header-anchor" href="#day1-12-11" aria-label="Permalink to &quot;day1 - 12.11&quot;">​</a></h1><p>今天是第一天练习 vim，看不用鼠标能多流畅</p><h2 id="光标移动" tabindex="-1">光标移动 <a class="header-anchor" href="#光标移动" aria-label="Permalink to &quot;光标移动&quot;">​</a></h2><ol><li><p>i 光标进入前，I 光标进入首行;a 光标进入后，A 光标进入尾行;o 光标进入下一行，O 光标进入上一行</p></li><li><p>hjkl 为左下上右</p></li><li><p>be 为跳单词</p></li><li><p>^进入行首，$进入行尾</p></li><li><p>gg 移动到首行，G 移动到尾行</p></li><li><p>ctrl + f 屏幕向下移动一页 b 往回 back 一页 d 往下 down 半页 u 网上 up 半页</p></li></ol><h2 id="搜索替换" tabindex="-1">搜索替换 <a class="header-anchor" href="#搜索替换" aria-label="Permalink to &quot;搜索替换&quot;">​</a></h2><ol><li><p>/target，?target 在光标当前位置以上或者以下寻找字符串</p></li><li><p>n 重复前一个搜索动作，N 反向前一个搜索动作</p></li><li><p>:m,ns/word1/word2/g --&gt; m 到 n 行间将 word1 替换为 word2</p></li></ol><h2 id="复制粘贴" tabindex="-1">复制粘贴 <a class="header-anchor" href="#复制粘贴" aria-label="Permalink to &quot;复制粘贴&quot;">​</a></h2><ol><li>dd 剪切,ndd 剪切 n 行</li><li>yy 复制，nyy 复制 n 行</li><li>p/P 向下/上复制</li><li>J 光标所在行与下一行拼接成同一行</li></ol><h2 id="底线命令" tabindex="-1">底线命令 <a class="header-anchor" href="#底线命令" aria-label="Permalink to &quot;底线命令&quot;">​</a></h2><ol><li>:w 保存文件， :w!强制保存</li><li>:q/q!退出与不保存退出</li><li>wq 保存退出</li><li>set xxx 设置环境变量，如 set nu 显示行号</li></ol>',10)]))}const u=l(o,[["render",r]]);export{m as __pageData,u as default};
